import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material';
import About from './pages/About';
import JSONUpload from './pages/JSONUpload';
import LandingPage from './pages/LandingPage';
import ImageBar from './pages/ImageBar';
import TopBar from './components/TopBar'; // Import the new component
import './styles.css';

const theme = createTheme({
    palette: {
        primary: {
            main: '#0D47A1',
        },
        secondary: {
            main: '#FFC107',
        },
        text: {
            primary: '#000000',
        },
    },
    typography: {
        fontFamily: '"Roboto", sans-serif',
    },
});

function App() {
    return (
        <Router>
            <ThemeProvider theme={theme}>
                <div>
                    <TopBar /> {/* Use the new component */}

                    {/* Add padding to offset the fixed TopBar height */}
                    <div style={{ paddingTop: '5rem' }}>
                        <Routes>
                            <Route path="/json-upload" element={<JSONUpload />} />
                            <Route
                                path="/"
                                element={
                                    <div>
                                        <LandingPage />
                                        <section id="about" style={{ padding: '100px 20px', marginBottom: '10px' }}>
                                            <About />
                                        </section>
                                        <ImageBar />
                                        <section id="experience" style={{ padding: '100px 20px' }}>
                                            <Experience />
                                        </section>
                                        <section id="projects" style={{ padding: '100px 20px' }}>
                                            <Projects />
                                        </section>
                                        <section id="awards" style={{ padding: '100px 20px' }}>
                                            <Awards />
                                        </section>
                                    </div>
                                }
                            />
                        </Routes>
                    </div>
                </div>
            </ThemeProvider>
        </Router>
    );
}

export default App;
