import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { HashLink } from 'react-router-hash-link';
import { AppBar, createTheme, Tab, Tabs, ThemeProvider } from '@mui/material';
import About from './pages/About'; // Updated import
import JSONUpload from './pages/JSONUpload';
import LandingPage from './pages/LandingPage';
import ImageBar from './pages/ImageBar'; // Import the new component
import './styles.css';

const theme = createTheme({
    palette: {
        primary: {
            main: '#0D47A1',
        },
        secondary: {
            main: '#FFC107',
        },
        text: {
            primary: '#000000',
        },
    },
    typography: {
        fontFamily: '"Roboto", sans-serif',
    },
});

function App() {
    return (
        <Router>
            <ThemeProvider theme={theme}>
                <div>
                    <AppBar
                        position="static"
                        sx={{
                            backgroundColor: '#FFFFFF',
                            height: '5rem',
                            boxShadow: 'none',
                            borderBottom: '1px solid #E0E0E0',
                        }}
                    >
                        <div style={{
                            display: 'flex',
                            justifyContent: 'flex-end',
                            alignItems: 'center',
                            width: '100%',
                            height: '100%'
                        }}>
                            <Tabs
                                sx={{
                                    '& .MuiTab-root': {
                                        fontSize: '1rem',
                                        fontFamily: theme.typography.fontFamily,
                                        minWidth: 'auto',
                                        padding: '0 1rem',
                                        color: theme.palette.text.primary,
                                        '&:hover': {
                                            color: theme.palette.primary.main,
                                        },
                                        '&.Mui-selected': {
                                            color: theme.palette.primary.main,
                                            fontWeight: 'bold',
                                        }
                                    }
                                }}
                            >
                                <Tab label="About" component={HashLink} to="#about" /> {/* Updated label */}
                                <Tab label="Experience" component={HashLink} to="#experience" />
                                <Tab label="Projects" component={HashLink} to="#projects" />
                                <Tab label="Awards" component={HashLink} to="#awards" />
                            </Tabs>
                        </div>
                    </AppBar>

                    <Routes>
                        <Route path="/json-upload" element={<JSONUpload />} />
                        <Route
                            path="/"
                            element={
                                <div>
                                    <LandingPage />
                                    <section id="about" style={{ padding: '100px 20px', marginBottom: '10px' }}>
                                        <About /> {/* Updated component */}
                                    </section>
                                    <ImageBar /> {/* Include the Image Bar */}
                                </div>
                            }
                        />
                    </Routes>
                </div>
            </ThemeProvider>
        </Router>
    );
}

export default App;
