import React from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import Home from './pages/Home';
import Projects from './pages/Projects';
import Experience from './pages/Experience';
import Awards from './pages/Awards';
import JSONUpload from './pages/JSONUpload';

import { AppBar, createTheme, Tab, Tabs, ThemeProvider } from '@mui/material';
import './styles.css';

const theme = createTheme({
    palette: {
        primary: {
            main: '#0D47A1',
        },
        secondary: {
            main: '#FFC107',
        },
        text: {
            primary: '#000000',
        },
    },
    typography: {
        fontFamily: '"Roboto", sans-serif',
    },
});

function App() {
    return (
        <Router>
            <ThemeProvider theme={theme}>
                <div>
                    <AppBar
                        position="static"
                        sx={{
                            backgroundColor: '#FFFFFF',
                            height: '5rem',
                            boxShadow: 'none',
                            borderBottom: '1px solid #E0E0E0',
                        }}
                    >
                        <div style={{
                            display: 'flex',
                            justifyContent: 'flex-end',
                            alignItems: 'center',
                            width: '100%',
                            height: '100%'
                        }}>
                            <Tabs
                                sx={{
                                    '& .MuiTab-root': {
                                        fontSize: '1rem',
                                        fontFamily: theme.typography.fontFamily,
                                        minWidth: 'auto',
                                        padding: '0 1rem',
                                        color: theme.palette.text.primary,
                                        '&:hover': {
                                            color: theme.palette.primary.main,
                                        },
                                        '&.Mui-selected': {
                                            color: theme.palette.primary.main,
                                            fontWeight: 'bold',
                                        }
                                    }
                                }}
                            >
                                <Tab label="JSON Upload" component={Link} to="/json-upload" />
                                <Tab label="Experience" component={Link} to="/experience" />
                                <Tab label="Projects" component={Link} to="/projects" />
                                <Tab label="Awards" component={Link} to="/awards" />
                                <Tab label="Home" component={Link} to="/" />
                            </Tabs>
                        </div>
                    </AppBar>

                    <Routes>
                        <Route path="/" element={<Home />} />
                        <Route path="/projects" element={<Projects />} />
                        <Route path="/experience" element={<Experience />} />
                        <Route path="/awards" element={<Awards />} />
                        <Route path="/json-upload" element={<JSONUpload />} />
                    </Routes>
                </div>
            </ThemeProvider>
        </Router>
    );
}

export default App;
